CREATE TABLE "address" ("address_id" INTEGER GENERATED BY DEFAULT AS IDENTITY NOT NULL, "address" VARCHAR(50) NOT NULL, "address2" VARCHAR(50), "district" VARCHAR(20) NOT NULL, "city_id" SMALLINT NOT NULL, "postal_code" VARCHAR(10), "phone" VARCHAR(20) NOT NULL, "last_update" TIMESTAMP WITHOUT TIME ZONE DEFAULT NOW() NOT NULL, CONSTRAINT "address_pkey" PRIMARY KEY ("address_id"));
