CREATE TABLE "film" ("film_id" INTEGER GENERATED BY DEFAULT AS IDENTITY NOT NULL, "title" VARCHAR(255) NOT NULL, "description" TEXT, "release_year" text NULL, "language_id" SMALLINT NOT NULL, "rental_duration" SMALLINT DEFAULT 3 NOT NULL, "rental_rate" numeric(4, 2) DEFAULT 4.99 NOT NULL, "length" SMALLINT, "replacement_cost" numeric(5, 2) DEFAULT 19.99 NOT NULL, "rating" text DEFAULT 'G', "last_update" TIMESTAMP WITHOUT TIME ZONE DEFAULT NOW() NOT NULL, "special_features" TEXT[], "fulltext" TSVECTOR NOT NULL, CONSTRAINT "film_pkey" PRIMARY KEY ("film_id"));
