CREATE TABLE "staff" ("staff_id" INTEGER GENERATED BY DEFAULT AS IDENTITY NOT NULL, "first_name" VARCHAR(45) NOT NULL, "last_name" VARCHAR(45) NOT NULL, "address_id" SMALLINT NOT NULL, "email" VARCHAR(50), "store_id" SMALLINT NOT NULL, "active" BOOLEAN DEFAULT TRUE NOT NULL, "username" VARCHAR(16) NOT NULL, "password" VARCHAR(40), "last_update" TIMESTAMP WITHOUT TIME ZONE DEFAULT NOW() NOT NULL, "picture" BYTEA, CONSTRAINT "staff_pkey" PRIMARY KEY ("staff_id"));
